// problem 2

func max(x ...int) int {
	mi := 0
	for i := 1; i < len(x); i++ {
		if x[i] > x[mi] {
			mi = i
		}
	}
	return x[mi]
}

func sum(x ...int) int {
	res := 0
	for _, i := range x {
		res += i
	}
	return res
}

func min(x, y int) int {
	if x < y {
		return x
	}
	return y
}

func maxStarSum(vals []int, edges [][]int, k int) int {
	if k == 0 {
		return max(vals...)
	}

	graph := make(map[int]map[int]int)
	for _, e := range edges {
		if graph[e[0]] == nil {
			graph[e[0]] = make(map[int]int)
		}
		if vals[e[1]] > 0 {
			graph[e[0]][e[1]] = vals[e[1]]
		}

		if graph[e[1]] == nil {
			graph[e[1]] = make(map[int]int)
		}
		if vals[e[0]] > 0 {
			graph[e[1]][e[0]] = vals[e[0]]
		}
	}

	res := math.MinInt

	for i := range vals {
		es := make([]int, 0)
		for _, w := range graph[i] {
			es = append(es, w)
		}
		sort.Slice(es, func(i, j int) bool {
			return es[i] > es[j]
		})
		size := min(k, len(es))
		res = max(res, vals[i]+sum(es[:size]...))
	}

	return res
}

